"use strict";
var $header_top = $(".header"),
    $nav = $(".header__nav");
$header_top.find("a").on("click", function () {
    $(this).parent().toggleClass("open-menu");
}),
    $("#fullpage").fullpage({
        sectionSelector: ".section",
        navigation: !0,
        slidesNavigation: !0,
        controlArrows: !1,
        anchors: ["welcome", "about", "compound", "takein", "questions", "other"],
        menu: "#menu",
        responsive: !0,
        responsiveHeight: 800,
        // scrollBar: true,
        // fitToSection: false,
        // autoScrolling: false,
        onLeave: function (index, nextIndex, direction) {
            $(".navbar__nav").removeClass("open"), console.log(index);
        },
    }),
    $(".section__compound .owl-carousel").owlCarousel({
        items: 4,
        margin: 20,
        nav: !0,
        dots: !1,
        responsiveRefreshRate: 100,
        responsive: { 0: { items: 1 }, 640: { items: 2 }, 1000: { items: 3 }, 1200: { items: 4 } },
        navText: ['<svg class="icon"><use xlink:href="assets/img/sprite.svg?#prev"></use></svg>', '<svg class="icon"><use xlink:href="assets/img/sprite.svg?#next"></use></svg>'],
    }),
    $(".compound__item a").magnificPopup({ type: "inline", fixedBgPos: false, fixedContentPos: false, overflowY: "scroll" }),
    $(".navbar__toggle").click(function () {
        $(".navbar__nav").toggleClass("open");
    }),
    $(".navbar__nav .active a").click(function () {
        $(".navbar__nav").removeClass("open");
    }),
    $(".navbar__nav a").click(function () { });
